% Program to test the volume of the beep for stop signal% Changed to use PsychPortAudio instead of SndInitializePsychSound(0)samp = 22254;aud_stim = sin(1:0.25:1000);pahandle = PsychPortAudio('Open', [],[],[],samp,1);PsychPortAudio('FillBuffer', pahandle, aud_stim);PsychPortAudio('Start', pahandle,1);PsychPortAudio('Stop', pahandle, 1);